import React,{useState} from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogActions from "@material-ui/core/DialogActions";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import TextField from '@material-ui/core/TextField';


export default function CustomerUpdate(props) {
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  function updateCustomer() {
    // const url = "/api/customers/" + props.id;
    // fetch(url, {
    //   method: "UPDATE"
    // }).then(props.refreshState());
    console.log('UPDATE')
    setOpen(false);
  }

  const [form, setName] = useState({props})


function updateField(e){
    e.preventDefault();
    console.log(e.target.value)
    setName({
        ...form,
        [e.target.name]: e.target.value
      });
    };

  return (
    <div>
      <Button variant="contained"  onClick={handleClickOpen}>
        수정
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle onClose={handleClose}>수정</DialogTitle>
        <DialogContent>
          <Typography gutterBottom>선택한 고객 정보를 수정합니다.</Typography>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="이름"
            type="email"
            fullWidth
            onChange={updateField}

          />
        </DialogContent>
        <DialogActions>
          <Button variant="contained" color="primary" onClick={updateCustomer}>
            수정
          </Button>
          <Button variant="outlined" color="primary" onClick={handleClose}>
            닫기
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
